	(5,1,'2017-06-12 14:07:44','2017-06-12 14:07:44','Welcome to WordPress. This is your first post. Edit or delete it, then start writing!','Hello world!','','inherit','closed','closed','','1-revision-v1','','','2017-06-12 14:07:44','2017-06-12 14:07:44','',1,'http://coderintros.dev/2017/06/1-revision-v1/',0,'revision','',0),
	(6,1,'2017-06-12 14:08:17','2017-06-12 14:08:17','Welcome to WordPress. This is your first post. Edit or delete it, then start writing!','Hello world!','','inherit','closed','closed','','1-revision-v1','','','2017-06-12 14:08:17','2017-06-12 14:08:17','',1,'http://coderintros.dev/2017/06/1-revision-v1/',0,'revision','',0),
